mapper.select:
select cart_id as cartId
from cart
where user_id = #{userId}

select count(*)
from cart_items ci join cart c on ci.cart_id = c.cart_id
where ci.cart_item_id = #{cartItemId}
    and c.user_id = #{userId}

select cart_item_id
from cart_items ci join cart c on ci.cart_id = c.cart_id
where c.user_id = #{userId}
    and ci.product_id = #{productId}

update cart_items
set quantity = #{quantity},
    is_selected = #{isSelected}
where cart_item_id = #{cartItemId}

mapper.update:
update cart_items ci
join cart c on ci.cart_id = c.cart_id
set ci.is_selected = #{selectAll}
where c.user_id = #{userId}

mapper.select:
select address1, address2, name, phone, zipcode
from users
where user_id = #{userId}
